import React, { useState, useEffect } from 'react';
import "./sidebarStyle.css"


export function Sidebar({isSidebarVisible,userImageURL}) {
    const [isvisible, setIsVisible] = useState(false);
    useEffect(()=>{setIsVisible( isSidebarVisible )

    },[isSidebarVisible]
    
    )

  
    const toggleVisibility = () => {
      setIsVisible(false);
    }
  
    return (
      <div className={`sidebar ${isvisible ? 'visible' : 'hidden'}`}>
        <button onClick={toggleVisibility}><i class="fa-solid fa-arrow-right"></i></button>
        <div className="nav-links">
          <div className="user-profile>" div/>
          <img src={userImageURL} alt="User" />
        <li><a href="default.asp">Mapa</a></li>
    <li><a href="about.asp">Analiza danych</a></li>
    <li><a href="news.asp">Czujniki</a></li>
    <li><a href="contact.asp">Powiadomienia</a></li>
    <li><a href="about.asp">Ocena aplikacji</a></li>
    <li><a href="about.asp">Ustawienia</a></li>
        </div>
      </div>
    );
  }
  
  export default Sidebar;
