import "./layout.css"

export function Layout({ children }) {
  const myStyle = {
    color: "white",
    backgroundColor: "#a0a0a0",
    padding: "10px",
    fontFamily: "Sans-Serif",
  };
  const navigationStyle = {
    listStyleType: "none",
    backgroundColor: "#deb4cb",
    padding:"20px",
    
  }
  const linkStyle = {
    '&::hover': {
      backgroundColor: 'blue',
  }
}


  return (
    <div>
      <div style={myStyle}>
        <ul style={navigationStyle}>
          <li>
            <a href="default.asp">Home</a>
          </li>
          <li>
            <a style={linkStyle} href="news.asp">News</a>
            
          </li>
          <li>
            <a href="contact.asp">Contact</a>
          </li>
          <li>
            <a href="about.asp">About</a>
          </li>
        </ul>
        <br />
        Tekst
      </div>
      <main>{children}</main>
    </div>
  );
}
